DROP TABLE IF EXISTS `vcos_country`;
CREATE TABLE `vcos_country` (
  `country_id` int(11) NOT NULL AUTO_INCREMENT,
  `country_code` varchar(32) DEFAULT NULL,
  `country_number` varchar(32) DEFAULT NULL COMMENT '数字代号',
  `country_name` varchar(128) DEFAULT NULL,
  `country_name_en` varchar(128) DEFAULT NULL,
  `country_short_code` char(3) DEFAULT NULL COMMENT '3 Character',
  `country_state` int(1) DEFAULT '0' COMMENT '状态 0可用',
  `mark` varchar(128) DEFAULT NULL,
  `create_by` varchar(32) DEFAULT NULL,
  `create_time` int(11) DEFAULT '0',
  PRIMARY KEY (`country_id`),
  KEY `country_code` (`country_short_code`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=496 DEFAULT CHARSET=utf8 COMMENT='国家表';

